mapper.select:
SELECT DISTINCT c.contract_id
FROM contract c
WHERE 1=1


    AND c.contract_id IN

        #{contractId}




AND COALESCE(c.subscribed_at, '1900-01-01') <= #{billingEndDate}
AND COALESCE(c.terminated_at, '9999-12-31') > #{billingStartDate}
AND COALESCE(c.initially_subscribed_at, '1900-01-01') <= #{billingEndDate}
AND COALESCE(c.preffered_termination_date, '9999-12-31') > #{billingStartDate}
ORDER BY c.contract_id

SELECT DISTINCT c.contract_id
FROM contract c
WHERE 1=1

AND c.contract_id % #{partitionCount} = #{partitionKey}


AND COALESCE(c.subscribed_at, '1900-01-01') <= #{billingEndDate}
AND COALESCE(c.terminated_at, '9999-12-31') > #{billingStartDate}
AND COALESCE(c.initially_subscribed_at, '1900-01-01') <= #{billingEndDate}
AND COALESCE(c.preffered_termination_date, '9999-12-31') > #{billingStartDate}
ORDER BY c.contract_id

SELECT DISTINCT c.contract_id FROM contract c WHERE c.contract_id IN
#{contractId}
ORDER BY c.contract_id
