WHERE 1=1
AND c.contract_id IN

    #{contractId}



AND COALESCE(c.subscribed_at, DATE '1900-01-01') <= #{billingEndDate}
AND COALESCE(c.terminated_at, DATE '9999-12-31') > #{billingStartDate}
AND COALESCE(c.initially_subscribed_at, DATE '1900-01-01') <= #{billingEndDate}
AND COALESCE(c.preffered_termination_date, DATE '9999-12-31') > #{billingStartDate}


AND TRUNC(COALESCE(p.effective_start_date_time, TO_TIMESTAMP('1900-01-01 00:00:00', 'YYYY-MM-DD HH24:MI:SS'))) 
    < TRUNC(COALESCE(p.effective_end_date_time, TO_TIMESTAMP('9999-12-31 23:59:59', 'YYYY-MM-DD HH24:MI:SS')))
AND COALESCE(p.effective_start_date_time, TO_TIMESTAMP('1900-01-01 00:00:00', 'YYYY-MM-DD HH24:MI:SS')) 
    <= (TRUNC(#{billingEndDate}) + 1 - (1/86400))
AND COALESCE(p.effective_end_date_time, TO_TIMESTAMP('9999-12-31 23:59:59', 'YYYY-MM-DD HH24:MI:SS')) 
    > (TRUNC(#{billingStartDate}) + 1 - (1/86400))
AND COALESCE(p.subscribed_at, DATE '1900-01-01') <= #{billingEndDate}
AND COALESCE(p.terminated_at, DATE '9999-12-31') > #{billingStartDate}


AND (s.contract_id IS NULL OR (
TRUNC(COALESCE(s.effective_start_date_time, TO_TIMESTAMP('1900-01-01 00:00:00', 'YYYY-MM-DD HH24:MI:SS'))) 
    < TRUNC(COALESCE(s.effective_end_date_time, TO_TIMESTAMP('9999-12-31 23:59:59', 'YYYY-MM-DD HH24:MI:SS')))
AND COALESCE(s.effective_start_date_time, TO_TIMESTAMP('1900-01-01 00:00:00', 'YYYY-MM-DD HH24:MI:SS')) 
    <= (TRUNC(#{billingEndDate}) + 1 - (1/86400))
AND COALESCE(s.effective_end_date_time, TO_TIMESTAMP('9999-12-31 23:59:59', 'YYYY-MM-DD HH24:MI:SS')) 
    > (TRUNC(#{billingStartDate}) + 1 - (1/86400))
))
